import './App.css';
import Header from './components/Header';
import Nav from './components/Nav';
import Footer from './components/Footer';
import Content from './components/Content';

const menu = [
  {
    title: 'item1',
    link: '/item1'
  },
  {
    title: 'item2',
    link: '/item2'
  },
  {
    title: 'item3',
    link: '/item3'
  },
  {
    title: 'item4',
    link: '/item4'
  },
  {
    title: 'item5',
    link: '/item5'
  }
];

const style = {
  display: 'flex',
  justifyContent: 'center',
  alignItems: 'center',
  color: 'dark',
  backgroundColor: 'lightBlue',
  width: '700px',
  height: '50px',
  innerHeight: '30px',
  borderRadius: '5px'
}

function App() {
  return (
    <div className="App">
      <Header></Header>
      <Nav items={menu}></Nav>
      <Footer>2021</Footer>
      <Content style={style}>Это светлое уведомление — check it out!</Content>
    </div>
  );
}

export default App;
